var base = require('../../lib/orm/base');



/**
 * Object representing a Check.
 * @constructor
 * @param {Object} attributes Attributes to set in the resulting object.
 */
var Check = function(attributes) {
  Check.super_.call(this, Check, attributes);
};


/**
 * Fields on the Check object.
 * @return {Object} Fields on the entity object.
 */
Check.fields = {
  'label': null,
  'entity_id': null
};


/**
 * Add properties that help map to cassandra
 * complex types
 */
Check.meta = {
  name: 'Check',
  cname: 'checks',
  columnFamily: 'objects',
  prefix: 'ch',
  parents: ['entity_id'],
  estimatedChildCount: 5
};


/**
 * Version of the model which is written to the database.
 */
Check.operationalVersion = 0;


base.inheritBase(Check, __filename);


/**
 * Check object.
 */
exports.Check = Check;
