var path = require('path');

var sprintf = require('sprintf').sprintf;

/**
 * Version of the migration framework.
 * @type {String}
 * @const
 */
exports.VERSION = '0.1';

/**
 * Migration file name pattern.
 * @type {Object}
 * @const
 */
exports.MIGRATION_FILENAME_PATTERN = /\.js$/;

/**
 * Template for the migration file name.
 * @type {String}
 * @const
 */
exports.MIGRATION_FILE_NAME = '%(version)s_%(slug)s.js';

/**
 * Name of the initial migration file.
 * @type {String}
 * @const
 */
exports.INITIAL_MIGRATION_FILENAME = sprintf(exports.MIGRATION_FILE_NAME, {'version': '0000', 'slug': 'initial'});
